import 'package:carousel_slider/carousel_slider.dart';import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:flutter_rating_bar/flutter_rating_bar.dart';class ReviewView extends StatefulWidget {  const ReviewView({super.key});  @override  State<ReviewView> createState() => _ReviewViewState();}class _ReviewViewState extends State<ReviewView> {  @override  Widget build(BuildContext context) {    return Scaffold(      appBar: AppBar(        title: Text('Flutter Enthusiast | UI Craftsman'),        centerTitle: true,      ),      body: SingleChildScrollView(        child: Center(          child: Column(            children: <Widget>[              CarouselSlider(                items: [                  Container(                    margin: EdgeInsets.only(top: 35),                    width: 285,                    height: 300,                    child: ClipRRect(                      borderRadius: BorderRadius.circular(10),                      child: Image(                        fit: BoxFit.cover,                        image: AssetImage(                          'assets/images/product-1.jpg',                        ),                      ),                    ),                  ),                  Container(                    margin: EdgeInsets.only(top: 35),                    width: 285,                    height: 300,                    child: ClipRRect(                      borderRadius: BorderRadius.circular(10),                      child: Image(                        fit: BoxFit.cover,                        image: AssetImage(                          'assets/images/product-2.jpg',                        ),                      ),                    ),                  ),                  Container(                    margin: EdgeInsets.only(top: 35),                    width: 285,                    height: 300,                    child: ClipRRect(                      borderRadius: BorderRadius.circular(10),                      child: Image(                        fit: BoxFit.cover,                        image: AssetImage(                          'assets/images/product-4.jpg',                        ),                      ),                    ),                  ),                ],                options: CarouselOptions(                  enlargeCenterPage: true,                  aspectRatio: 1.0,                  autoPlay: true,                ),              ),              Container(                margin: EdgeInsets.only(top: 20),                child: Text(                  'Curology',                  style: TextStyle(                    fontSize: 35,                    fontFamily: 'PoppinsB',                  ),                ),              ),              Container(                child: Text(                  'the cleans',                  style: TextStyle(                    fontSize: 19,                    fontFamily: 'PoppinsL',                  ),                ),              ),              Container(                margin: EdgeInsets.only(top: 15),                child: RatingBar.builder(                  initialRating: 3,                  minRating: 1,                  direction: Axis.horizontal,                  allowHalfRating: true,                  itemCount: 5,                  itemPadding: EdgeInsets.symmetric(horizontal: 2.0),                  itemBuilder: (context, _) => Icon(                    Icons.star_rate_rounded,                    color: Colors.amber,                  ),                  onRatingUpdate: (rating) {                    print(rating);                  },                ),              ),              Container(                margin: EdgeInsets.only(left: 35, right: 35, top: 25),                child: TextFormField(                  maxLines: 3,                  decoration: InputDecoration(                    hintText: 'Write your review',                    hintStyle: TextStyle(                      fontFamily: 'PoppinsL',                    ),                    suffixIcon: Icon(                      CupertinoIcons.wand_stars,                      size: 31,                    ),                    border: OutlineInputBorder(                      borderRadius: BorderRadius.circular(18),                    ),                  ),                ),              ),              Container(                margin: EdgeInsets.only(top: 35),                width: 250,                height: 55,                child: ElevatedButton(                  style: ElevatedButton.styleFrom(                    shape: RoundedRectangleBorder(                      borderRadius: BorderRadius.circular(8),                    ),                    backgroundColor: Colors.deepPurple.shade300,                  ),                  onPressed: () {},                  child: Text(                    'Submit',                    style: TextStyle(                      fontSize: 18,                      fontFamily: 'PoppinsB',                      color: Colors.white,                    ),                  ),                ),              )            ],          ),        ),      ),    );  }}